/**
 * 颜色输入模糊搜索
 */
function ColorSearch(id) {
    this.id = id;
    this.name ='.dimResultDiv';
}
$.extend(ColorSearch.prototype, {
    init: function() {
          var id = this.id,
              name = this.name,
              _this = this;
          $(id).bind('input propertychange', function() {
              _this.render();
          });
    },
    render: function() {
          var _this = this,
              id = this.id,
              name = this.name;
          window.dimThis = this;
          var val = $.trim($(id).val()); 
          if (val == '') {
              $(name).hide().html("");
              return;
          }
         //遍历颜色数组的每一个值,判断val是否indexof > -1
         var html = '',
            arr = merchantColors.split(','),
            val1 = val.toLowerCase();
         $.each(arr,function(i,v){
            var v1 = v.toLowerCase();
            if(v1.indexOf(val1)>-1){
                html += "<div class='dimResultDivItem' onmouseenter='dimThis.getFocus(this)' onClick='dimThis.getCon(this);'>" + v + "</div>";
            }
         });
         if (html != '') {
            $(name).show().html(html);
        } else {
            $(name).hide().html('');
        }
    },
    getFocus: function(obj) {
        $(".dimResultDivItem").removeClass("addbg");
        $(obj).addClass("addbg");
    },
    getCon: function(obj) {
        var id = this.id,
            name = this.name;
        var value = $(obj).text();
        $(id).val(value);
        $(name).hide().html("");
    }
});